func (c *current) onSetStmt1(varName, expr, asType any) (any, error) {
	value, ok := expr.(Value)
	if !ok {
		return nil, errors.New("invalid expression")
	}
	if asType != nil {
		dataType := asType.([]interface{})[3]
		switch dataType.(string) {
		case "Integer":
			value.Type = TypeInt
			value.Int = int32(value.Float)
		case "String":
			value.Type = TypeString
		case "Boolean":
			value.Type = TypeBoolean
		}
	}
	setValue(varName.(string), value)
	return nil, nil
}

func (p *parser) callonSetStmt1() (any, error) {
	stack := p.vstack[len(p.vstack)-1]
	_ = stack
	return p.cur.onSetStmt1(stack["varName"], stack["expr"], stack["asType"])
}

func (c *current) onDefStmt1(varName, dataType any) (any, error) {
	var value Value
	switch dataType.(string) {
	case "Integer":
		value = Value{Type: TypeInt}
	case "Float":
		value = Value{Type: TypeFloat}
	case "String":
		value = Value{Type: TypeString}
	case "Boolean":
		value = Value{Type: TypeBoolean}
	default:
		return nil, errors.New("unsupported type: " + dataType.(string))
	}
	setValue(varName.(string), value)
	return nil, nil
}

func (p *parser) callonDefStmt1() (any, error) {
	stack := p.vstack[len(p.vstack)-1]
	_ = stack
	return p.cur.onDefStmt1(stack["varName"], stack["dataType"])
}

func (c *current) onIfStmt1(condition, thenExpr, elseExpr any) (any, error) {
	condValue, ok := condition.(bool)
	if !ok {
		return nil, errors.New("invalid condition")
	}
	if condValue {
		return thenExpr, nil
	}
	return elseExpr, nil
}

func (p *parser) callonIfStmt1() (any, error) {
	stack := p.vstack[len(p.vstack)-1]
	_ = stack
	return p.cur.onIfStmt1(stack["condition"], stack["thenExpr"], stack["elseExpr"])
}

