func (c *current) onArithmeticExpr2(left, op, right any) (any, error) {
	leftValue, ok1 := left.(Value)
	rightValue, ok2 := right.(Value)
	if !ok1 || !ok2 {
		return nil, errors.New("invalid arithmetic operands")
	}
	result := Value{Type: TypeFloat}
	switch op.(string) {
	case "+":
		result.Float = toFloat64(leftValue) + toFloat64(rightValue)
	case "-":
		result.Float = toFloat64(leftValue) - toFloat64(rightValue)
	}
	return result, nil
}

func (p *parser) callonArithmeticExpr2() (any, error) {
	stack := p.vstack[len(p.vstack)-1]
	_ = stack
	return p.cur.onArithmeticExpr2(stack["left"], stack["op"], stack["right"])
}

