func (c *current) onFunctionCall1(expr any) (any, error) {
	value, ok := expr.(Value)
	if !ok {
		return nil, errors.New("invalid print argument")
	}
	switch value.Type {
	case TypeInt:
		fmt.Printf("%d\n", value.Int)
	case TypeFloat:
		fmt.Printf("%f\n", value.Float)
	case TypeString:
		fmt.Printf("%s\n", value.String)
	case TypeBoolean:
		fmt.Printf("%t\n", value.Boolean)
	case TypeNull:
		fmt.Println("Null")
	}
	return nil, nil
}

